    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        /** Variable Naming Standard-
        * <page><component><orderofappear>
        * page={SignIn, Reg, User, Final}
        * component={Name, Photo, E_Mail, Photo etc}
        * orderofappear={1,2 etc} [eg-RegName1 i.e First_Name in Register Page]
        */
        // TODO add your handling code here:

        try {
            String sql="Select * from member_lps";
            Class.forName ("com.mysql.jdbc.Driver");
            Connection con=(Connection)DriverManager.getConnection("jdbc:mysql://sql6.freemysqlhosting.net:3306/sql6100273", "sql6100273", "sgTCHQBbhD");
            Statement stmt=con.createStatement();
            ResultSet rs =stmt.executeQuery(sql);
            String user= SignInEmail.getText();
            String pwd= new String (SignInPass.getPassword());
            while(rs.next()){String uname=rs.getString("E_Mail");
                String password =rs.getString("Password");
                if((user.equals (uname)) && (pwd.equals(password))){
                    {
                        jDialog1.setVisible(true);
                    passcheck.setVisible(false);
                    this.dispose();
                    }
                    
                }
                else {

                    passcheck.setText("Incorrect Username or Password!");

                }
               
            }
        }
        catch (Exception e) {
            JOptionPane.showMessageDialog(null, "No Internet Connection");
        }
        this.dispose();
        JOptionPane.showMessageDialog(null, "Window Has Closed Due To Wrong Password/UserName");

        
    }//GEN-LAST:event_jButton1ActionPerformed

